import React, { useState } from 'react';
import { TelegramClient } from 'telegram';
import { StringSession } from 'telegram/sessions';
import './App.css';

function App() {
    const [tab, setTab] = useState('auth');
    const [apiId, setApiId] = useState('');
    const [apiHash, setApiHash] = useState('');
    const [phone, setPhone] = useState('');
    const [code, setCode] = useState('');
    const [client, setClient] = useState(null);
    const [authStatus, setAuthStatus] = useState({ text: '', type: '' });
    const [mediaItems, setMediaItems] = useState([]);
    const [progress, setProgress] = useState(0);
    const [chatInput, setChatInput] = useState('');
    const [isWaitingForCode, setIsWaitingForCode] = useState(false);

    const connect = async () => {
        try {
            // Validate phone number
            const cleanPhone = phone.trim();
            if (!cleanPhone.match(/^\+?\d{10,15}$/)) {
                throw new Error('Invalid phone number format. Use +1234567890');
            }

            setAuthStatus({ text: 'Connecting...', type: 'loading' });
            setIsWaitingForCode(false); // Reset verification state

            const newClient = new TelegramClient(
                new StringSession(''),
                parseInt(apiId),
                apiHash,
                { connectionRetries: 3 }
            );

            await newClient.start({
                phoneNumber: cleanPhone,
                phoneCode: async () => {
                    setAuthStatus({ text: 'Enter verification code sent to your phone', type: 'info' });
                    return new Promise((resolve) => {
                        const interval = setInterval(() => {
                            if (code) {
                                clearInterval(interval);
                                resolve(code);
                            }
                        }, 500);
                    });
                },
                onError: (err) => {
                    setAuthStatus({ text: `Error: ${err.message}`, type: 'error' });
                },
            });

            setClient(newClient);
            setAuthStatus({ text: 'Connected!', type: 'success' });
            setTab('download');
        } catch (err) {
            setAuthStatus({ text: `Error: ${err.message}`, type: 'error' });
        }
    };

    const fetchMedia = async () => {
        if (!chatInput) return;

        try {
            setProgress(10);
            const entity = await client.getInputEntity(chatInput);
            setProgress(50);
            const messages = await client.getMessages(entity, { limit: 100 });
            setProgress(80);

            const items = messages
                .filter(m => m.media)
                .map(m => ({
                    id: m.id,
                    type: m.photo ? 'photo' : m.video ? 'video' : 'file',
                    filename: m.photo ? `photo_${m.id}.jpg` :
                        m.video ? `video_${m.id}.mp4` :
                            `file_${m.id}.dat`,
                    message: m
                }));

            setMediaItems(items);
            setProgress(100);
        } catch (err) {
            setAuthStatus({ text: `Fetch error: ${err.message}`, type: 'error' });
        }
    };

    const downloadFile = async (mediaItem) => {
        try {
            const buffer = await client.downloadMedia(mediaItem.message);
            const blob = new Blob([buffer]);
            const url = URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = mediaItem.filename;
            a.click();
            URL.revokeObjectURL(url);
        } catch (err) {
            setAuthStatus({ text: `Download failed: ${err.message}`, type: 'error' });
        }
    };

    return (
        <div className="App">
            <div className="tabs">
                <button 
                    onClick={() => setTab('auth')} 
                    className={`tab ${tab === 'auth' ? 'active' : ''}`}
                >
                    Authentication
                </button>
                <button 
                    onClick={() => client && setTab('download')} 
                    className={`tab ${tab === 'download' ? 'active' : ''}`}
                    disabled={!client}
                >
                    Download Media
                </button>
                <button 
                    onClick={() => setTab('about')} 
                    className={`tab ${tab === 'about' ? 'active' : ''}`}
                >
                    About
                </button>
            </div>

            <div className="tab-content">
                {tab === 'auth' && (
                    <div className="auth-container">
                        <h2>Telegram Login</h2>
                        <input
                            placeholder="API ID (from my.telegram.org)"
                            value={apiId}
                            onChange={(e) => setApiId(e.target.value)}
                            type="text"
                        />
                        <input
                            placeholder="API Hash"
                            value={apiHash}
                            onChange={(e) => setApiHash(e.target.value)}
                            type="password"
                        />
                        <input
                            placeholder="Phone (+1234567890)"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            type="tel"
                        />
                        <button onClick={connect} className="connect-btn">
                            Connect to Telegram
                        </button>

                        {authStatus.text && (
                            <div className={`status ${authStatus.type}`}>
                                {authStatus.text}
                            </div>
                        )}

                        {authStatus.text.includes('verification') && (
                            <div className="verification-container">
                                <input
                                    placeholder="Verification Code"
                                    value={code}
                                    onChange={(e) => setCode(e.target.value)}
                                    type="text"
                                />
                            </div>
                        )}
                    </div>
                )}

                {tab === 'download' && client && (
                    <div className="download-container">
                        <h2>Download Media</h2>
                        <div className="search-container">
                            <input
                                placeholder="Chat ID or @username"
                                value={chatInput}
                                onChange={(e) => setChatInput(e.target.value)}
                                type="text"
                            />
                            <button onClick={fetchMedia} className="fetch-btn">
                                Fetch Media
                            </button>
                        </div>

                        <div className="media-grid">
                            {mediaItems.length > 0 ? (
                                mediaItems.map(item => (
                                    <div key={item.id} className="media-item">
                                        <div className="media-preview">
                                            {item.type === 'photo' ? 'ðŸ“·' : item.type === 'video' ? 'ðŸŽ¥' : 'ðŸ“„'}
                                            <span className="filename">{item.filename}</span>
                                        </div>
                                        <button 
                                            onClick={() => downloadFile(item)}
                                            className="download-btn"
                                        >
                                            Download
                                        </button>
                                    </div>
                                ))
                            ) : (
                                <div className="empty-state">
                                    {progress > 0 && progress < 100 ? (
                                        'Loading media...'
                                    ) : (
                                        'No media found. Enter a chat ID and click "Fetch Media"'
                                    )}
                                </div>
                            )}
                        </div>

                        {progress > 0 && progress < 100 && (
                            <div className="progress-container">
                                <div className="progress-bar" style={{ width: `${progress}%` }}>
                                    {progress}%
                                </div>
                            </div>
                        )}
                    </div>
                )}

                {tab === 'about' && (
                    <div className="about-container">
                        <h2>About This App</h2>
                        <p>This application allows you to download media from Telegram chats.</p>
                        <h3>How to use:</h3>
                        <ol>
                            <li>Get your API ID and Hash from my.telegram.org</li>
                            <li>Enter your credentials in the Authentication tab</li>
                            <li>After connecting, go to Download Media tab</li>
                            <li>Enter a chat ID or username to fetch media</li>
                        </ol>
                        <div className="disclaimer">
                            Note: This app runs entirely in your browser. Your credentials are not stored or sent to any server.
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
