import React, { useState, useRef, useEffect } from 'react';
import { TelegramClient } from 'telegram';
import { StringSession } from 'telegram/sessions';
import { saveAs } from 'file-saver';
import './App.css';

function App() {
  const [tab, setTab] = useState('auth');
  const [apiId, setApiId] = useState('');
  const [apiHash, setApiHash] = useState('');
  const [phone, setPhone] = useState('');
  const [code, setCode] = useState('');
  const [client, setClient] = useState(null);
  const [authStatus, setAuthStatus] = useState({ text: '', type: '' });
  const [mediaItems, setMediaItems] = useState([]);
  const [progress, setProgress] = useState(0);
  const [chatInput, setChatInput] = useState('');
  const [isWaitingForCode, setIsWaitingForCode] = useState(false);
  const [isDownloading, setIsDownloading] = useState(false);
  const [selectedItems, setSelectedItems] = useState(new Set());
  const verificationResolver = useRef(null);

  useEffect(() => {
    return () => {
      if (client) {
        client.disconnect();
      }
    };
  }, [client]);

  const connect = async () => {
    try {
      if (!apiId.match(/^\d+$/)) throw new Error('API ID must be numeric');
      if (!apiHash.match(/^[a-f0-9]{32}$/i)) throw new Error('Invalid API Hash format');
      const cleanPhone = phone.trim();
      if (!cleanPhone.match(/^\+?\d{10,15}$/)) throw new Error('Invalid phone number format');

      setAuthStatus({ text: 'Connecting...', type: 'loading' });
      setIsWaitingForCode(false);
      setCode('');

      const sessionKey = 'telegramSession';
      const savedSession = localStorage.getItem(sessionKey) || '';
      const newClient = new TelegramClient(
        new StringSession(savedSession),
        parseInt(apiId),
        apiHash,
        { 
          connectionRetries: 3,
          useWSS: true,
          downloadWorkers: 4
        }
      );

      await newClient.connect();
      await newClient.start({
        phoneNumber: cleanPhone,
        phoneCode: async () => {
          setAuthStatus({ text: 'Enter verification code', type: 'info' });
          setIsWaitingForCode(true);
          return new Promise((resolve) => {
            verificationResolver.current = resolve;
          });
        },
        onError: (err) => {
          setAuthStatus({ text: `Auth error: ${err.message}`, type: 'error' });
          setIsWaitingForCode(false);
        },
      });

      const session = newClient.session.save();
      localStorage.setItem(sessionKey, session);
      setClient(newClient);
      setAuthStatus({ text: 'Connected!', type: 'success' });
      setTab('download');
    } catch (err) {
      setAuthStatus({ text: `Error: ${err.message}`, type: 'error' });
      setIsWaitingForCode(false);
    }
  };

  const verifyCode = () => {
    if (!code.trim()) {
      setAuthStatus({ text: 'Enter verification code', type: 'error' });
      return;
    }
    if (verificationResolver.current) {
      verificationResolver.current(code);
      verificationResolver.current = null;
      setIsWaitingForCode(false);
    }
  };

  const fetchMedia = async () => {
    if (!chatInput.trim() || !client) {
      setAuthStatus({ text: 'Enter chat ID/username', type: 'error' });
      return;
    }

    try {
      setProgress(10);
      setAuthStatus({ text: 'Fetching media...', type: 'loading' });
      const entity = await client.getInputEntity(chatInput.trim());
      setProgress(50);
      const messages = await client.getMessages(entity, { limit: 100 });
      setProgress(80);

      const items = messages
        .filter((m) => m.media)
        .map((m) => {
          let filename = 'file';
          let fileType = 'file';

          if (m.photo) {
            filename = `photo_${m.id}.jpg`;
            fileType = 'photo';
          } else if (m.video) {
            filename = `video_${m.id}.mp4`;
            fileType = 'video';
          } else if (m.document) {
            const fileAttr = m.document.attributes.find((attr) => attr.className === 'DocumentAttributeFilename');
            filename = fileAttr ? fileAttr.fileName : `file_${m.id}`;
            fileType = m.document.mimeType || 'file';
          }
          return {
            id: m.id,
            type: fileType,
            filename,
            message: m,
            size: m.media.document?.size || 0,
            date: new Date(m.date * 1000).toLocaleString(),
          };
        });

      setMediaItems(items);
      setProgress(100);
      setAuthStatus({ text: `Found ${items.length} items`, type: 'success' });
      setTimeout(() => setProgress(0), 1000);
    } catch (err) {
      setAuthStatus({ text: `Error: ${err.message}`, type: 'error' });
      setProgress(0);
    }
  };

  const downloadFile = async (mediaItem) => {
    if (!client || !mediaItem.message.media) return;

    try {
      setIsDownloading(true);
      setAuthStatus({ text: `Downloading ${mediaItem.filename}...`, type: 'loading' });

      const blob = await client.downloadMedia(mediaItem.message.media, {
        workers: 4,
        progressCallback: (received, total) => {
          setProgress(Math.round((received / total) * 100));
        },
        returnBlob: true
      });

      saveAs(blob, mediaItem.filename);
      setAuthStatus({ text: 'Download started!', type: 'success' });
    } catch (err) {
      setAuthStatus({ text: `Download failed: ${err.message}`, type: 'error' });
    } finally {
      setIsDownloading(false);
      setProgress(0);
    }
  };

  const downloadSelected = async () => {
    if (selectedItems.size === 0) {
      setAuthStatus({ text: 'Select files first', type: 'error' });
      return;
    }

    try {
      setIsDownloading(true);
      setAuthStatus({ text: `Downloading ${selectedItems.size} files...`, type: 'loading' });

      for (const id of selectedItems) {
        const item = mediaItems.find(m => m.id === id);
        if (item) await downloadFile(item);
      }
    } catch (err) {
      setAuthStatus({ text: `Error: ${err.message}`, type: 'error' });
    } finally {
      setIsDownloading(false);
    }
  };

  const toggleItemSelection = (id) => {
    const newSelected = new Set(selectedItems);
    newSelected.has(id) ? newSelected.delete(id) : newSelected.add(id);
    setSelectedItems(newSelected);
  };

  const selectAllItems = () => {
    setSelectedItems(selectedItems.size === mediaItems.length ? new Set() : new Set(mediaItems.map(item => item.id)));
  };

  const formatFileSize = (bytes) => {
    if (!bytes) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const disconnect = async () => {
    if (client) await client.disconnect();
    localStorage.removeItem('telegramSession');
    setClient(null);
    setTab('auth');
    setAuthStatus({ text: 'Disconnected', type: 'info' });
    setMediaItems([]);
    setSelectedItems(new Set());
  };

  return (
    <div className="App">
      <header className="app-header">
        <h1>Telegram Media Downloader</h1>
        {client && <button onClick={disconnect} className="disconnect-btn">Disconnect</button>}
      </header>

      <div className="tabs">
        <button onClick={() => setTab('auth')} className={`tab ${tab === 'auth' ? 'active' : ''}`}>
          Auth
        </button>
        <button onClick={() => setTab('download')} className={`tab ${tab === 'download' ? 'active' : ''}`} disabled={!client}>
          Downloads
        </button>
        <button onClick={() => setTab('about')} className={`tab ${tab === 'about' ? 'active' : ''}`}>
          About
        </button>
      </div>

      <div className="tab-content">
        {tab === 'auth' && (
          <div className="auth-container">
            <h2>Login</h2>
            <div className="input-group">
              <label>API ID</label>
              <input
                placeholder="From my.telegram.org"
                value={apiId}
                onChange={(e) => setApiId(e.target.value.trim())}
                type="text"
              />
            </div>
            <div className="input-group">
              <label>API Hash</label>
              <input
                placeholder="API Hash"
                value={apiHash}
                onChange={(e) => setApiHash(e.target.value.trim())}
                type="password"
              />
            </div>
            <div className="input-group">
              <label>Phone</label>
              <input
                placeholder="+1234567890"
                value={phone}
                onChange={(e) => setPhone(e.target.value.trim())}
                type="tel"
              />
            </div>
            <button onClick={connect} className="connect-btn" disabled={isWaitingForCode}>
              {isWaitingForCode ? 'Waiting...' : 'Connect'}
            </button>

            {isWaitingForCode && (
              <div className="verification-box">
                <div className="input-group">
                  <label>Code</label>
                  <input
                    placeholder="Verification code"
                    value={code}
                    onChange={(e) => setCode(e.target.value.trim())}
                    type="text"
                    autoFocus
                  />
                </div>
                <button onClick={verifyCode} className="verify-btn" disabled={!code.trim()}>
                  Verify
                </button>
              </div>
            )}

            {authStatus.text && <div className={`status ${authStatus.type}`}>{authStatus.text}</div>}
          </div>
        )}

        {tab === 'download' && client && (
          <div className="download-container">
            <h2>Download Media</h2>
            <div className="search-container">
              <div className="input-group">
                <label>Chat ID/@username</label>
                <input
                  placeholder="Chat ID or @username"
                  value={chatInput}
                  onChange={(e) => setChatInput(e.target.value)}
                  type="text"
                />
              </div>
              <button
                onClick={fetchMedia}
                className="fetch-btn"
                disabled={progress > 0 && progress < 100}
              >
                {progress > 0 ? 'Fetching...' : 'Fetch Media'}
              </button>
            </div>

            {mediaItems.length > 0 && (
              <div className="media-actions">
                <button onClick={selectAllItems} className="select-all-btn">
                  {selectedItems.size === mediaItems.length ? 'Deselect All' : 'Select All'}
                </button>
                <button
                  onClick={downloadSelected}
                  className="download-selected-btn"
                  disabled={selectedItems.size === 0 || isDownloading}
                >
                  Download ({selectedItems.size})
                </button>
              </div>
            )}

            <div className="media-grid">
              {mediaItems.length > 0 ? (
                mediaItems.map((item) => (
                  <div 
                    key={item.id} 
                    className={`media-item ${selectedItems.has(item.id) ? 'selected' : ''}`}
                    onClick={() => toggleItemSelection(item.id)}
                  >
                    <div className="media-preview">
                      {item.type.includes('image') ? 'ðŸ“·' : item.type.includes('video') ? 'ðŸŽ¥' : 'ðŸ“„'}
                    </div>
                    <div className="media-info">
                      <span className="filename" title={item.filename}>{item.filename}</span>
                      <span className="file-size">{formatFileSize(item.size)}</span>
                      <span className="file-date">{item.date}</span>
                    </div>
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        downloadFile(item);
                      }}
                      className="download-btn"
                      disabled={isDownloading}
                    >
                      Download
                    </button>
                  </div>
                ))
              ) : (
                <div className="empty-state">
                  {progress > 0 ? 'Loading...' : 'No media found'}
                </div>
              )}
            </div>

            {progress > 0 && (
              <div className="progress-container">
                <div className="progress-bar" style={{ width: `${progress}%` }}>
                  {progress}%
                </div>
              </div>
            )}

            {authStatus.text && <div className={`status ${authStatus.type}`}>{authStatus.text}</div>}
          </div>
        )}

        {tab === 'about' && (
          <div className="about-container">
            <h2>About</h2>
            <p>Download media from Telegram chats.</p>
            <ol>
              <li>Get API credentials from my.telegram.org</li>
              <li>Connect in the Auth tab</li>
              <li>Enter chat ID to fetch media</li>
              <li>Download files directly</li>
            </ol>
            <div className="disclaimer">
              Note: Runs in your browser. No server processing.
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
